/* HTML Global */

body {
    background-color: #1a2632;
    color: #fff;
}

p {
    color: #607d8b !important;
}

h4 {
    color: #fff;
}


/* Class Global */

.lead {
    color: #929292;
}


/* Main Site and New User */

.site-header {
    background-color: #243447;
}

.site-header-nav .nav-item {
    color: #fff;
}

.jumbotron-home {
    background-image: none;
    background-color: #1a2632;
}

.features-list .list-divider,
.setup-form .tos-info,
.setup-form .setup-organization-next {
    border-color: #304251;
}

.radio-label {
    border-color: #304251;
}

.tag-input-tag {
    background: #243447;
}

.tag-input-tag .remove {
    background: #1a2632;
}

.home-hero-signup .form-control-lg {
    border-color: #304251;
}

.shade-gray {
    background-color: #121b23 !important;
}

.tile-block {
    border-color: #304251;
}

.tile-bordered:not(:last-child) {
    border-color: #304251;
}


/* Main Site Login */

.session-authentication {
    background-color: #1a2632;
}

.session-authentication .header-logo {
    color: #fff;
}

.session-authentication .auth-form-body {
    border-color: #304251;
}

.auth-form-body {
    background-color: #1a2632;
}

.session-authentication .create-account-callout {
    border-color: #304251;
}

.session-authentication .auth-form-header {
    color: #fff;
}


/* Site Wide Global */

.header {
    background-color: #243447;
    border-color: #304251;
}

.header-logo-invertocat {
    color: #fff;
}

.header-search-scope {
    border-color: #304251;
}

.header-nav-link {
    color: #fff;
}


/*.scoped-search .form-control .header-search-scope {
    background-color: #1a2632;
    border-color: #304251;
}*/

.form-control,
.form-select {
    color: #fff;
    background-color: #151f29;
    border-color: #304251;
}

.form-control.focus,
.form-control:focus,
.form-select.focus,
.form-select:focus {
    box-shadow: none;
}

.form-group .form-control:focus {
    background-color: #151f29;
}

.btn {
    color: #fff;
    background-color: #1a2632;
    background-image: none;
    border-color: #304251;
}

.btn:hover,
.btn:active,
.btn.zeroclipboard-is-hover,
.btn.zeroclipboard-is-active {
    text-decoration: none;
    background-color: #151f29;
    background-image: none;
    border-color: #304251;
}

.btn:active,
.btn.selected,
.btn.zeroclipboard-is-active {
    background-color: #1a2632;
    background-image: none;
    border-color: #304251;
}

.btn-primary {
    color: #fff;
    background-color: #57BB5C;
    background-image: none;
    border-color: #57BB5C;
}

.btn-theme-green {
    background-color: #6cc644;
}

.site-footer {
    border-color: #304251;
}

.dropdown-menu {
    background-color: #1a2632;
    border: 1px solid #304251;
    box-shadow: none;
}

.dropdown-divider {
    background-color: #304251;
}

.dropdown-item {
    color: #9e9e9e;
}

.dropdown-item:hover {
    background-color: #243447;
}

button.dropdown-item.dropdown-signout {
    color: #df3e3e;
}


/* Site Related */

.setup-header {
    border-color: #304251;
}


/* Home */

.news .alert {
    border-color: #304251;
}

.news .alert .octicon {
    color: #8899a6;
}

.news .alert .branch-link,
.news .alert .pull-info {
    color: #151f29;
    background: #627790;
}

.ajax-pagination-form .ajax-pagination-d {
    background: #babdcb;
}

.boxed-group>h3,
.boxed-group .heading {
    color: #fff;
    background-color: #243447;
    border-color: #304251;
}

.boxed-group-inner {
    background: #1a2632;
    border: 1px solid #304251;
}

.mini-repo-list-item {
    border-color: #304251;
}

.mini-repo-list-item .repo-icon {
    color: #8899a6;
}

.private .mini-repo-list-item {
    background-color: #243447;
}

.private .mini-repo-list-item .repo-icon {
    color: #fff9c4;
}

.filter-bar {
    background-color: #1a2632;
    border-bottom: 1px solid #304251;
}

.boxed-group .counter {
    color: #fff;
    background-color: #1a2632;
    border: 1px #304251 solid;
}

.subscribe-feed {
    color: #6d6d72;
}

.protip-callout {
    border-color: #304251;
}

.octofication .broadcast-icon-mask {
    background-color: #1a2632;
}

.link-gray-dark {
    color: #4078c0 !important;
}

.more-repos {
    text-align: center;
    background-color: #243447;
    box-shadow: none;
    border-top: 1px solid #304251;
}


/* New Repo */

.outline-box-highlighted {
    background-color: #1a2632;
    border-color: #304251;
}

.outline-box {
    border: solid 1px #304251;
}

.copyable-terminal {
    background-color: #151f29;
}

.reponame-suggestion {
    color: #4caf50;
}


/* Repo Page (Main) */

.repohead.experiment-repo-nav {
    background-color: #151f29;
}

.pagehead {
    border-color: #304251;
}

.repository-meta {
    color: #9e9e9e;
}

.social-count {
    color: #607d8b;
    background-color: #151f29;
    border: 1px solid #304251;
    border-left: 0;
}

.user-mention,
.team-mention {
    color: #00aba5;
}

.reponav-item {
    color: #9e9e9e;
}

.reponav-item:hover,
.reponav-item:focus {
    color: #fff;
}

.counter {
    background-color: #151f29;
    border: 1px #304251 solid;
}

.reponav-item .counter {
    color: #f9f9f9;
}

.reponav-item.selected {
    color: #fff;
    background-color: #1a2632;
    border-color: #d28e5d #304251 transparent;
}

.btn.selected:hover {
    background-color: #151f29;
}

.overall-summary {
    border-color: #304251;
}

.text-emphasized {
    color: #fff;
}

.numbers-summary a,
.numbers-summary .nolink {
    color: #969ab0;
}

.repository-lang-stats-graph {
    border-color: #304251;
}

.repository-lang-stats ol.repository-lang-stats-numbers li .language-color {
    border: 1px #304251 solid;
}

.repository-lang-stats ol.repository-lang-stats-numbers li .lang {
    color: #fff;
}

table.files {
    background: #1a2632;
}

table.files tr.navigation-focus td {
    background: #151f29;
}

table.files td {
    border-color: #304251;
}

table.files td.icon {
    color: #fff;
}

.warning {
    padding: 0.5em;
    margin-bottom: 0.8em;
    font-weight: bold;
    background-color: #d28e5d;
}

.file-wrap {
    border-color: #304251;
}

.commit-author-section {
    color: #ccc;
}

.commit-tease {
    color: #8485ce;
    background-color: #243447;
    border-color: #3e5570;
}

.commits-list-item .commit-author {
    color: #00aba5;
}

.commit-tease-sha {
    color: #4078c0;
}

.commit-tease-contributors {
    background-color: #151f29;
    border-color: #3e5570;
}

.commit .commit-title a {
    color: #4078c0;
}

.branch-infobar {
    background-color: #1a2632;
    border-color: #3e5570;
}

.full-commit {
    background: #1a2632;
    border: 1px solid #304251;
}

.full-commit p.commit-title {
    color: #00aba5;
}

.full-commit .commit-meta {
    background: #243447;
    border-color: #304251;
}

.full-commit .sha-block {
    color: #fff;
}

.full-commit .sha-block>.sha {
    color: #9e9e9e;
}

.full-commit .btn-outline,
.full-commit .btn-outline:disabled {
    border-color: #304251;
}

.commits-list-item.navigation-focus {
    background: #151f29;
}

.file-navigation .get-repo-btn {
    border-color: #304251;
}

.file-navigation .get-repo-btn:first-child {
    border-color: #304251;
}

.signed-commit-badge {
    border-color: #304251;
}

.signed-commit-signer-name .signer {
    display: block;
    font-weight: bold;
    color: #00aba5;
}

.flex-table-item-primary {
    color: #fff;
}

span.d-block {
    color: #fff;
}

.signed-commit-header {
    background-color: #243447;
    border-color: #304251;
}


/* Repo Blame */

.blame-commit-title,
.blame-commit-title .message {
    color: #4078c0;
}

.blame-commit,
.blame-commit+.blame-line {
    border-color: #304251;
}

.blame-blob-num {
    background-color: #243447;
}

.muted-link {
    color: #00aba5 !important;
}


/* README Markdown Live */

.readme .markdown-body,
.readme .plain {
    background-color: #151f29;
    border-color: #304251;
}

.markdown-body .highlight pre,
.markdown-body pre {
    background-color: #1a2632;
    border: 1px #304251 solid;
}

.markdown-body h1 {
    border-color: #304251;
}

.markdown-body img {
    background-color: #151f29;
}

.markdown-body h1 .octicon-link,
.markdown-body h2 .octicon-link,
.markdown-body h3 .octicon-link,
.markdown-body h4 .octicon-link,
.markdown-body h5 .octicon-link,
.markdown-body h6 .octicon-link {
    color: #fff;
}

.markdown-body table tr {
    background-color: #1a2632;
}

.markdown-body table th,
.markdown-body table td {
    border-color: #304251;
}

.markdown-body table tr:nth-child(2n) {
    background-color: #243447;
}

.markdown-body pre code,
.markdown-body pre tt {
    color: #969ab0;
}

.markdown-body code,
.markdown-body tt {
    color: #151f29;
    background-color: #627790;
}

.blob-num {
    color: #5e6b77;
    border-color: #304251;
}

.blob-code-inner {
    color: #6d6d72;
}

.blob-code-hunk,
.blob-code-expandable {
    background-color: #243447;
}

.blob-num-hunk,
.blob-num-expandable {
    background-color: #243447;
}

.blob-num-expandable {
    background-color: #243447;
}

.ace_editor .ace_gutter-active-line {
    background-color: #243447;
}

.ace_editor .ace_marker-layer .ace_active-line {
    background-color: #243447;
}


/* Code syntax Highlighting */

.pl-pds,
.pl-s,
.pl-s .pl-pse .pl-s1,
.pl-sr,
.pl-sr .pl-cce,
.pl-sr .pl-sra,
.pl-sr .pl-sre {
    color: #4078c0;
}

.pl-s .pl-s1,
.pl-smi {
    color: #bbb;
}

.pl-k {
    color: #b73999;
}

.ace_string {
    color: #4670d8;
}

.ace_support.ace_type {
    color: #b73999;
}

.ace_keyword {
    color: #8485ce;
}

.ace_variable {
    color: #d28e5d;
}

.ace_focus {
    background-color: #000;
}

.ace_markup.ace_heading {
    color: #4670d8;
}


/* Markdown Standalone */

.markdown-body h2 {
    border-color: #304251;
}

.markdown-body hr {
    background-color: #304251;
}

.markdown-body blockquote {
    border-color: #304251;
}


/* Repo Code Tab Live | Markdown */

.file {
    border-color: #304251;
}

.file-header {
    background-color: #1a2632;
    border-color: #304251;
}

.breadcrumb strong.final-path {
    color: #929292;
}

.ace_gutter {
    background: #1a2632;
    border-color: #304251;
}

.ace-github-light {
    color: #9e9e9e;
    background-color: #1a2632;
}

.file-commit-form .commit-form {
    border-color: #304251;
}

.commit-sha {
    background-color: inherit;
    border-color: #304251;
}


/* Repo | Issues Tab */

.table-list-header {
    background-color: #1a2632;
    border-color: #304251;
}

.Box-body-row--highlight .Box-row-link {
    color: #00aba5;
}

.Box-body-row {
    border-top: 1px solid #304251;
}

.Box-body-row--highlight.navigation-focus,
.Box-body-row--highlight:hover {
    background-color: #151f29;
}

.issues-listing .table-list-issues .navigation-focus {
    background-color: #1a2632;
}

.table-list {
    border-color: #304251;
}

.table-list-cell {
    border-top-color: #304251;
}

.table-list-bordered .table-list-cell:first-child {
    border-left-color: #304251;
}

.table-list-bordered .table-list-cell:last-child {
    border-right-color: #304251;
}

.table-list-header-toggle .btn-link {
    color: #9e9e9e;
}

.table-list-header-toggle .btn-link:hover {
    color: #fff;
}

.table-list-header-toggle .btn-link.selected,
.table-list-header-toggle .btn-link.selected:hover {
    color: #fff;
}

.issue-title-link {
    color: #00aba5;
}

.protip {
    color: #6d6d72;
}

.milestone-title-link a {
    color: #cecece;
}

.Box-body {
    border-color: #304251;
}


/* Dropdown Menu */

.select-menu-modal {
    border-color: #304251;
    box-shadow: none;
}

.select-menu-header {
    background: #243447;
    border-color: #304251;
}

.select-menu-header .select-menu-title {
    color: #9e9e9e;
    text-shadow: none;
}

.select-menu-header {
    background: #243447;
    border-bottom: 1px solid #304251;
}

.select-menu-item {
    background-color: #1a2632;
    border-color: #304251;
}

.select-menu-item.selected {
    color: #fff;
}

.select-menu-filters {
    background-color: #1a2632;
}

.select-menu-text-filter:first-child:last-child {
    border-color: #304251;
}

.select-menu-text-filter input {
    border-color: #304251;
}


/* Repo | Pull Request Tab */

.border-left,
.border-right,
.border-bottom {
    border-color: #304251 !important;
}

.pr-toolbar.is-stuck::after {
    border-color: #304251;
    box-shadow: none;
}


/* Repo Issues & Pull Request Convo */

.gh-header-edit .edit-issue-title,
.gh-header-edit .edit-issue-title:focus {
    background-color: #151f29;
}

.gh-header-meta {
    border-color: #304251;
}

.gh-header-meta .author {
    color: #00aba5;
}

.commit-ref {
    color: #151f29;
    background-color: #627790;
}

.commit-ref .user {
    color: #151f29;
}

.tabnav-tab.selected {
    background-color: #1a2632;
}

.tabnav-pr .tabnav-tab.selected {
    color: #fff;
    border-color: #304251;
}

.tabnav-pr {
    border-color: #304251;
}

.timeline-comment-wrapper {
    border: 0;
}

.timeline-comment-header {
    background-color: #243447;
    border-color: #304251;
}

.timeline-comment-header .author {
    color: #00aba5;
}

.timeline-comment {
    background-color: #1a2632;
    border-color: #304251;
}

.timeline-comment-wrapper>.timeline-comment::before,
.timeline-new-comment .timeline-comment::before {
    border-right-color: #304251;
}

timeline-comment::before {
    border-right-color: #243447 !important;
}

.timeline-comment::after {
    border-right-color: #243447 !important;
}

.timeline-comment.current-user .timeline-comment-header {
    background-color: #243447;
    border-color: #304251;
}

.timeline-comment.current-user {
    border-color: #304251;
}

.timeline-comment.current-user .timeline-comment-label {
    border-color: #304251;
}

.timeline-comment-wrapper .timeline-comment.current-user::before {
    border-width: 8px;
    border-right-color: #304251;
}

.timeline-comment-label {
    border-color: #304251;
}

.comment-reactions.has-reactions {
    border-color: #304251;
}

.reaction-summary-item {
    border-color: #304251;
}

.timeline-commits .commit-message>code a {
    color: #8485ce;
}

.discussion-timeline::before {
    width: 1px;
    background-color: #304251;
}

.discussion-timeline-actions {
    background-color: #1a2632;
    border: 0;
}

.discussion-item-ref-title .title-link {
    color: #8485ce;
}

.discussion-item-entity {
    color: #4078c0;
}

.discussion-item+.discussion-item {
    border-color: #304251;
}

.discussion-sidebar-item+.discussion-sidebar-item {
    border-color: #304251;
}

.discussion-item-icon {
    color: #8899a6;
    background-color: #243447;
    border-color: #304251;
}

.discussion-item+.discussion-item-review {
    border-color: #304251;
}

.discussion-item-review .file-header {
    background-color: #1a2632;
    border-color: #304251;
}

.discussion-item-review+.discussion-item {
    border-top: 1px solid #304251;
}

.discussion-item-review .blob-wrapper {
    border-color: #304251;
}

.review-comment-contents::after,
.review-comment.is-comment-editing::after {
    background-color: #304251;
}

.new-discussion-timeline .previewable-comment-form .comment-form-head.tabnav {
    background: #243447;
}

.new-discussion-timeline .previewable-comment-form .comment-body {
    border-color: #304251;
}

.new-discussion-timeline .closed-banner {
    background: #304251;
    border-bottom: 18px solid #1a2632;
}

.suggester {
    background: #243447;
    border-color: #304251;
}

.suggester li {
    border-color: #304251;
}

.tabnav {
    border-color: #304251;
}

.tabnav-tab.selected {
    color: #fff;
    border-color: #304251;
}

.previewable-comment-form textarea {
    background-color: #1a2632;
}

.input-contrast:focus {
    background: #151f29;
}

.upload-enabled textarea {
    border-color: #304251;
}

.drag-and-drop {
    background-color: #1a2632;
    border-color: #304251;
}

.commit-icon .octicon {
    background-color: #1a2632;
}

.issues-listing .table-list-issues .navigation-focus {
    background-color: #151f29;
}

.branch-action-state-clean .branch-action-body::after {
    border-right-color: #6cc644;
}

.branch-action-state-unstable .branch-action-body::after {
    border-right-color: #cea61b;
}

.branch-action-body {
    background-color: #1a2632;
}

.branch-action-body .merge-message,
.branch-action-body .merge-branch-form {
    background-color: #243447;
    border-top: solid 1px #304251;
}

.branch-action-state-merged .branch-action-body {
    border-color: #6e5494;
}

.merge-status-list {
    border-color: #304251;
}

.merge-status-item {
    background-color: #243447;
    border-color: #304251;
}

.branch-group-heading {
    background: #243447;
    border-color: #304251;
}

.branch-action-item+.branch-action-item {
    border-color: #304251;
}

.status-heading {
    color: #607d8b;
}


/* Site | Issue Tab */

.Box-row--focus-gray.navigation-focus {
    background-color: #1a2632;
}

.Box-row:first-of-type {
    border-color: #304251;
}

.Box-row {
    border-color: #304251;
}

a.Box-row-link.h4.js-navigation-open {
    color: #4078c0;
}


/* Code Tab | Branches */

.branch-summary,
.branch-group-heading+.branch-summary,
.branch-summary {
    border-color: #304251;
}

.branch-a-b-count .meter {
    background-color: #243447;
}

.branch-a-b-count .count-half:last-child {
    border-color: #304251;
}

.count-value.count-behind {
    color: #dc3c3c;
}

.count-value.count-ahead {
    color: #52bd58;
}

a.branch-name {
    color: inherit;
}

.table-of-contents li+li {
    border-color: #304251;
}

.commit-desc+.commit-branches {
    border-color: #304251;
}


/* File Changed | Compare | Reviews */

.diffbar {
    background-color: #1a2632;
}

.diff-table tr:not(:last-child) .line-comments {
    border-color: #304251;
}

.pr-toolbar {
    background-color: #1a2632;
}

.range-editor {
    background-color: #1a2632;
    border-color: #304251;
}

.compare-pr-placeholder {
    background-color: #1a2632;
    border-color: #cea61b;
}

ul.comparison-list {
    background: #1a2632;
    border-color: #304251;
}

ul.comparison-list>li.title {
    color: #9e9e9e;
    background: #243447;
}

ul.comparison-list>li {
    border-color: #304251;
}

.file-diff-split .empty-cell {
    background-color: #1a2632;
    border-right-color: #304251;
}

.feature-callout-octicon {
    border-color: #304251;
}

.feature-callout {
    border-color: #304251;
}

.review-thread {
    border-color: #304251;
}

.review-thread-reply {
    background-color: #243447;
    border-color: #304251;
